$(document).ready(function () {

    $('.j-menu-trigger').on('click', function () {
        $('.main-wrapper, .main-nav').toggleClass('vis-menu');
        $(this).toggleClass('is-active');
    });
    var navLink = $('.main-nav_list__item a')
        navLink.on('click', function(){
        $('.main-wrapper, .main-nav').removeClass('vis-menu');
            $('.j-menu-trigger').removeClass('is-active');
    });

    var $page = $('html, body');
    $('a[href*="#"]').click(function(){
        $page.animate({
            scrollTop: $($.attr(this, 'href')).offset().top
        }, 600);
        return false; });

    if($page.scrollTop() == 0){
        $('.scroll-top').removeClass('visible');
    }

});

var scrollTop = $(window).scrollTop();

$(window).scroll(function() {
    if($(window).scrollTop() < scrollTop && $(window).scrollTop() >= 500){
        $('.scroll-top').addClass('visible');
        scrollTop = $(window).scrollTop();
    } else{
        $('.scroll-top').removeClass('visible');
        scrollTop = $(window).scrollTop();
    }
});

function hiddTransition() {
    $('.main-nav').removeClass('transition');
    setTimeout(function(){
        $('.main-nav').addClass('transition');
    }, 1000);
}

$(window).on("load resize", function () {
    hiddTransition();

    if ($(window).width() <= 1199) {
        $('.j-carousel-1').owlCarousel({
            items: 1,
            // dots: false,
            // autoplay:true,
            autoplayTimeout: 3000,
            autoplayHoverPause: true,
            smartSpeed:1000,
            loop: true,
            // autoWidth: true,
            responsive: {
                450: {
                    margin: -100
                },
                580: {
                    items: 2,
                    margin: 80
                },
                640: {
                    items: 2,
                    margin: -50
                },
                900: {
                    items: 3,
                    margin: 50
                }
            }
        });
    } else {
        $('.j-carousel-1').owlCarousel('destroy')
    }
});


